import { createSlice } from '@reduxjs/toolkit';

// Исходное состояние фильтра
const initialFilterState = '';

// Создание среза фильтра с использованием createSlice
const filterSlice = createSlice({
  name: 'filter', // Имя среза фильтра
  initialState: initialFilterState, // Начальное состояние фильтра
  reducers: {
    changeFilter(_, action) {
      return action.payload; // Обновление значения фильтра на основе переданного действия
    },
  },
});

// Экспорт действия changeFilter из среза фильтра
export const { changeFilter } = filterSlice.actions;

// Экспорт редьюсера фильтра из среза фильтра
export const filterReducer = filterSlice.reducer;
